CREATE TABLE IF NOT EXISTS doctor (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, nume TEXT, parola TEXT, specializare TEXT);
CREATE TABLE IF NOT EXISTS pacient (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, nume TEXT, parola TEXT, inaltime TEXT, greutate TEXT, sex TEXT);
CREATE TABLE IF NOT EXISTS orar (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, doctor_id INTEGER, data TEXT, durataMin INTEGER, FOREIGN KEY(doctor_id) REFERENCES Doctor(id));
CREATE TABLE IF NOT EXISTS programare (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, orar_id INTEGER, pacient_id INTEGER status TEXT, FOREIGN KEY(orar_id) REFERENCES Orar(id), FOREIGN KEY(pacient_id) REFERENCES Pacient(id));
CREATE TABLE IF NOT EXISTS programare (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, orar_id INTEGER, pacient_id INTEGER status TEXT, FOREIGN KEY(orar_id) REFERENCES Orar(id), FOREIGN KEY(pacient_id) REFERENCES Pacient(id));
CREATE TABLE IF NOT EXISTS analiza (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, programare_id INTEGER, file TEXT, FOREIGN KEY(programare_id) REFERENCES Programare(id));